import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ListGroup from 'react-bootstrap/ListGroup';
import Spinner from 'react-bootstrap/Spinner';
import { withRouter } from 'react-router-dom';
import Login from './Login';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

function ListCourses(props) {
  const [data, setData] = useState([]);
  const [showLoading, setShowLoading] = useState(true);
  const apiUrl = "http://localhost:3000/api/courses";
  const apiAddCourseUrl = "http://localhost:3000/api/addCourse";

  useEffect(() => {
    const fetchData = async () => {
      axios.get(apiUrl)
        .then(result => {
          console.log('result.data:', result.data)

          console.log('data in if:', result.data)
          setData(result.data);
          setShowLoading(false);

        }).catch((error) => {
          console.log('error in fetchData:', error)
        });
    };
    fetchData();
  }, []);

  const showDetail = (id) => {

    props.history.push({
      pathname: '/showCourse/' + id
    });
  }

  const addCourse = (courseId) => {
    setShowLoading(true);

    console.log("apiAddCourseUrl >>> ", apiAddCourseUrl + "/" + courseId);
    console.log("courseId >>> ", courseId);


    axios.post(apiAddCourseUrl+"/"+courseId, courseId)
    .then((result) => {
        setShowLoading(false);
        // console.log('results from save course:',result.data)
        // props.history.push('/showCourse/' + result.data._id)

    }).catch((error) => setShowLoading(false));
  }

  return (
    <div>
      {data.length !== 0
        ? <div>
          {showLoading && <Spinner animation="border" role="status">
            <span className="sr-only">Loading...</span>
          </Spinner>}
          {/* <ListGroup>
            {data.map((item, idx) => (
              <ListGroup.Item key={idx} action onClick={() => { showDetail(item._id) }}>{item.courseName}</ListGroup.Item>
            ))}
          </ListGroup> */}

          <h2>List of Courses</h2><br />

          <Table responsive>
            <thead>
              <tr>
                <th scope="col">Code</th>
                <th scope="col">Name</th>
                <th scope="col">Section</th>
                <th scope="col">Semester</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, idx) => (
                <tr>
                  <td>{item.courseCode}</td>
                  <td>{item.courseName}</td>
                  <td>{item.section}</td>
                  <td>{item.semester}</td>
                  <td>
                    <Button onClick={() => { showDetail(item._id) }}>Detail</Button>
                    <Button onClick={() => { addCourse(item._id) }}>Add Course</Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
        : < Login />
      }
    </div>

  );
}
//
export default withRouter(ListCourses);
